  "mitigations": [
    {
      "id": "M1042",
      "name": "Disable or Remove Feature or Program",
      "description": "<p>Disable the SSH daemon on systems that do not require it. For macOS ensure Remote Login is disabled under Sharing Preferences.<sup><a href='https://sarah-edwards-xzkc.squarespace.com/blog/2020/4/30/analysis-of-apple-unified-logs-quarantine-edition-entry-6-working-from-home-remote-logins' target='_blank'>[21]</a></sup></p>"
    },
    {
      "id": "M1032",
      "name": "Multi-factor Authentication",
      "description": "<p>Require multi-factor authentication for SSH connections wherever possible, such as password protected SSH keys.</p>"
    },
    {
      "id": "M1018",
      "name": "User Account Management",
      "description": "<p>Limit which user accounts are allowed to login via SSH.</p>"
    }
