[
    {
        "id": "T1074.002",
        "name": "Remote Data Staging",
        "platforms": ["IaaS", "Linux", "Windows", "macOS"],
        "tactic": "Collection",
        "sub_technique_of": "T1074",
        "version": "1.1",
        "created": "13 March 2020",
        "last_modified": "08 March 2021",
        "contributors": ["Praetorian"],
        "description": "Adversaries may stage data collected from multiple systems in a central location or directory on one system prior to Exfiltration. Data may be kept in separate files or combined into one file through techniques such as Archive Collected Data. Interactive command shells may be used, and common functionality within cmd and bash may be used to copy data into a staging location.\n\nIn cloud environments, adversaries may stage data within a particular instance or virtual machine before exfiltration. An adversary may Create Cloud Instance and stage data in that instance. [1]\n\nBy staging data on one system prior to Exfiltration, adversaries can minimize the number of connections made to their C2 server and better evade detection.",
        "procedure_examples": [
            {
                "id": "G0007",
                "name": "APT28",
                "description": "APT28 has staged archives of collected data on a target's Outlook Web Access (OWA) server. [2]"
            },
            {
                "id": "S1043",
                "name": "ccf32",
                "description": "ccf32 has copied files to a remote machine infected with Chinoxy or another backdoor. [3]"
            },
            {
                "id": "G0114",
                "name": "Chimera",
                "description": "Chimera has staged stolen data on designated servers in the target environment. [4]"
            },
            {
                "id": "G0037",
                "name": "FIN6",
                "description": "FIN6 actors have compressed data from remote systems and moved it to another staging system before exfiltration. [5]"
            },
            {
                "id": "G0061",
                "name": "FIN8",
                "description": "FIN8 aggregates staged data from a network into a single location. [6]"
            },
            {
                "id": "G0065",
                "name": "Leviathan",
                "description": "Leviathan has staged data remotely prior to exfiltration. [7]"
            },
            {
                "id": "G0045",
                "name": "menuPass",
                "description": "menuPass has staged data on remote MSP systems or other victim networks prior to exfiltration. [8] [9]"
            },
            {
                "id": "G1019",
                "name": "MoustachedBouncer",
                "description": "MoustachedBouncer has used plugins to save captured screenshots to .\\AActdata\\ on an SMB share. [10]"
            },
            {
                "id": "C0002",
                "name": "Night Dragon",
                "description": "During Night Dragon, threat actors copied files to company web servers and subsequently downloaded them. [11]"
            },
            {
                "id": "C0024",
                "name": "SolarWinds Compromise",
                "description": "During the SolarWinds Compromise, APT29 staged data and files in password-protected archives on a victim's OWA server. [12]"
            },
            {
                "id": "G0027",
                "name": "Threat Group-3390",
                "description": "Threat Group-3390 has moved staged encrypted archives to Internet-facing servers that had previously been compromised with China Chopper prior to exfiltration. [13]"
            },
            {
                "id": "G1022",
                "name": "ToddyCat",
                "description": "ToddyCat manually transferred collected files to an exfiltration host using xcopy. [14]"
            }
        ],
        "mitigations": "This type of attack technique cannot be easily mitigated with preventive controls since it is based on the abuse of system features.",
        "detection": [
            {
                "id": "DS0017",
                "data_source": "Command",
                "data_component": "Command Execution",
                "detects": "Monitor executed commands and arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as Windows Management Instrumentation and PowerShell."
            },
            {
                "id": "DS0022",
                "data_source": "File",
                "data_component": "File Access",
                "detects": "Monitor processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib."
            },
            {
                "id": "DS0022-File Creation",
                "data_source": "File",
                "data_component": "File Creation",
                "detects": "Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging."
            }
        ],
