  "procedure_examples": [
    {
      "id": "G0114",
      "name": "Chimera",
      "description": "Chimera has registered alternate phone numbers for compromised users to intercept 2FA codes sent via SMS.",
      "url": "/groups/G0114",
      "reference": {
        "text": "Abusing cloud services to fly under the radar",
        "url": "https://research.nccgroup.com/2021/01/12/abusing-cloud-services-to-fly-under-the-radar/"
      }
    },
    {
      "id": "G0094",
      "name": "Kimsuky",
      "description": "Kimsuky has used a proprietary tool to intercept one-time passwords required for two-factor authentication.",
      "url": "/groups/G0094",
      "reference": {
        "text": "Phishing Target Reconnaissance and Attack Resource Analysis Operation Muzabi",
        "url": "https://www.boho.or.kr/krcert/publicationView.do?bulletin_writing_sequence=35936"
      }
    },
    {
      "id": "G1004",
      "name": "LAPSUS$",
      "description": "LAPSUS$ has replayed stolen session token and passwords to trigger simple-approval MFA prompts in hope of the legitimate user will grant necessary approval.",
      "url": "/groups/G1004",
      "references": {
        "text": "DEV-0537 Criminal Actor Targeting Organizations for Data Exfiltration and Destruction",
        "url": "https://www.microsoft.com/security/blog/2022/03/22/dev-0537-criminal-actor-targeting-organizations-for-data-exfiltration-and-destruction/"
      }
    },
    {
      "id": "C0014",
      "name": "Operation Wocao",
      "description": "During Operation Wocao, threat actors used a custom collection method to intercept two-factor authentication soft tokens.",
      "url": "/campaigns/C0014",
      "reference": {
        "text": "Operation Wocao: Shining a light on one of China’s hidden hacking groups.",
        "url": "https://www.fox-it.com/media/kadlze5c/201912_report_operation_wocao.pdf"
      }
    },
    {
      "id": "S1104",
      "name": "SLOWPULSE",
      "description": "SLOWPULSE can log credentials on compromised Pulse Secure VPNs during the DSAuth::AceAuthServer::checkUsernamePassword ACE-2FA authentication procedure.",
      "url": "/software/S1104",
      "reference": {
        "text": "Check Your Pulse: Suspected APT Actors Leverage Authentication Bypass Techniques and Pulse Secure Zero-Day.",
        "url": "https://www.mandiant.com/resources/blog/suspected-apt-actors-leverage-bypass-techniques-pulse-secure-zero-day"
      }
    },
    {
      "id": "S0018",
      "name": "Sykipot",
      "description": "Sykipot is known to contain functionality that enables targeting of smart card technologies to proxy authentication for connections to restricted network resources using detected hardware tokens.",
      "url": "/software/S0018",
      "reference": {
        "text": "Sykipot variant hijacks DOD and Windows smart cards.",
        "url": "https://www.alienvault.com/open-threat-exchange/blog/sykipot-variant-hijacks-dod-and-windows-smart-cards"
      }
    }
  ],
  "mitigations": [
    {
      "id": "M1017",
      "name": "User Training",
      "description": "Remove smart cards when not in use.",
      "url": "/mitigations/M1017"
    }
  ],
  "detection": [
    {
      "id": "DS0027",
      "source": "Driver",
      "component": "Driver Load",
      "description": "Monitor for use of proxied smart card connections by an adversary may be difficult because it requires the token to be inserted into a system; thus it is more likely to be in use by a legitimate user and blend in with other network behavior. Similar to Input Capture, keylogging activity can take various forms but can may be detected via installation of a driver.",
      "url": "/datasources/DS0027"
    },
    {
      "id": "DS0009",
      "source": "Process",
      "component": "OS API Execution",
      "description": "Monitor for API calls associated with polling to intercept keystrokes.",
      "url": "/datasources/DS0009"
    },
    {
      "id": "DS0024",
      "source": "Windows Registry",
      "component": "Windows Registry Key Modification",
      "description": "Monitor for changes to windows registry keys or values that may target multi-factor authentication mechanisms, such as smart cards, to gain access to credentials that can be used to access systems, services, and network resources.",
      "url": "/datasources/DS0024"
    }
  ],
