  "title": "Office Application Startup",
  "sub_techniques": [
    { "id": "T1137.001", "name": "Office Template Macros", "url": "/techniques/T1137/001/" },
    { "id": "T1137.002", "name": "Office Test", "url": "/techniques/T1137/002/" },
