    {"name": "Change the Normal template (Normal.dotm)", "url": "https://support.office.com/article/Change-the-Normal-template-Normal-dotm-06de294b-d216-47f6-ab77-ccb5166f98ea"},
    {"name": "Getting Started with VBA in Office", "url": "https://msdn.microsoft.com/en-us/vba/office-shared-vba/articles/getting-started-with-vba-in-office"},
    {"name": "Maintaining Access with normal.dotm", "url": "https://enigma0x3.net/2014/01/23/maintaining-access-with-normal-dotm/comment-page-1/"},
