      ],
      "description": "Adversaries may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms. This not only involves impairing preventative defenses, such as firewalls and anti-virus, but also detection capabilities that defenders can use to audit activity and identify malicious behavior. This may also span both native defenses as well as supplemental capabilities installed by users and administrators.",
      "details": {
        "platforms": ["Containers", "IaaS", "Linux", "Network", "Office 365", "Windows", "macOS"],
        "defense_bypassed": ["Anti-virus", "Digital Certificate Validation", "File monitoring", "Firewall", "Host forensic analysis", "Host intrusion prevention systems", "Log analysis", "Signature-based detection"],
        "version": "1.5",
        "created": "21 February 2020",
        "last_modified": "20 October 2023"
      },
      "version_links": {
        "permalink": "/versions/v15/techniques/T1562/",
        "live_version": "/versions/v15/techniques/T1562/"
      },
      "procedure_examples": [
        {
          "id": "G0059",
          "name": "Magic Hound",
          "description": "Magic Hound has disabled LSA protection on compromised hosts using reg add HKLM\\SYSTEM\\CurrentControlSet\\Control\\LSA /v RunAsPPL /t REG_DWORD /d 0 /f.",
          "ref": {
            "title": "DFIR Report. (2022, March 21). APT35 Automates Initial Access Using ProxyShell. Retrieved May 25, 2022.",
            "url": "https://thedfirreport.com/2022/03/21/apt35-automates-initial-access-using-proxyshell"
          }
        },
        {
          "id": "S0603",
          "name": "Stuxnet",
          "description": "Stuxnet reduces the integrity level of objects to allow write actions.",
          "ref": {
            "title": "Nicolas Falliere, Liam O Murchu, Eric Chien 2011, February W32.Stuxnet Dossier (Version 1.4) Retrieved. 2017/09/22",
            "url": "https://www.wired.com/images_blogs/threatlevel/2011/02/Symantec-Stuxnet-Update-Feb-2011.pdf"
          }
        }
      ],
      "mitigations": [
        {
          "id": "M1047",
          "name": "Audit",
          "description": "Routinely check account role permissions to ensure only expected users and roles have permission to modify defensive tools and settings."
        },
        {
          "id": "M1038",
          "name": "Execution Prevention",
          "description": "Use application control where appropriate, especially regarding the execution of tools outside of the organization's security policies (such as rootkit removal tools) that have been abused to impair system defenses. Ensure that only approved security applications are used and running on enterprise systems."
        },
        {
          "id": "M1022",
          "name": "Restrict File and Directory Permissions",
          "description": "Ensure proper process and file permissions are in place to prevent adversaries from disabling or interfering with security/logging services."
        },
        {
          "id": "M1024",
          "name": "Restrict Registry Permissions",
          "description": "Ensure proper Registry permissions are in place to prevent adversaries from disabling or interfering with security/logging services."
        },
        {
          "id": "M1054",
          "name": "Software Configuration",
          "description": "Consider implementing policies on internal web servers, such HTTP Strict Transport Security, that enforce the use of HTTPS/network traffic encryption to prevent insecure connections."
        },
        {
          "id": "M1018",
          "name": "User Account Management",
          "description": "Ensure proper user permissions are in place to prevent adversaries from disabling or interfering with security/logging services."
        }
      ],
      "detection": [
        {
          "id": "DS0025",
          "name": "Cloud Service",
          "components": [
            {
              "name": "Cloud Service Disable",
              "detects": "Monitor logs for API calls to disable logging. In AWS: StopLogging and DeleteTrail; in GCP: google.logging.v2.ConfigServiceV2.UpdateSink; in Azure: az monitor diagnostic-settings delete. Additionally, a sudden loss of a log source may indicate that it has been disabled."
            }
          ]
        },
        {
          "id": "DS0017",
          "name": "Command",
          "components": [
            {
              "name": "Command Execution",
              "detects": "Monitor executed commands and arguments that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms."
            }
          ]
        },
        {
          "id": "DS0027",
          "name": "Driver",
          "components": [
            {
              "name": "Driver Load",
              "detects": "Monitor for unusual/suspicious driver activity, especially regarding EDR and drivers associated with security tools as well as those that may be abused to disable security products."
            }
          ]
        },
        {
          "id": "DS0022",
          "name": "File",
          "components": [
            {
              "name": "File Deletion",
              "detects": "Monitor for missing log files hosts and services with known active periods."
            },
            {
              "name": "File Modification",
              "detects": "Monitor changes made to configuration files that contain settings for logging and defensive tools."
            }
          ]
        },
        {
          "id": "DS0018",
          "name": "Firewall",
          "components": [
            {
              "name": "Firewall Disable",
              "detects": "Monitor for changes in the status of the system firewall such as Windows Security Auditing events 5025 (The Windows firewall service has been stopped) and 5034 (The Windows firewall driver was stopped)."
            },
            {
              "name": "Firewall Rule Modification",
              "detects": "Monitor for changes made to firewall rules for unexpected modifications to allow/block specific network traffic that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms."
            }
          ]
        },
        {
          "id": "DS0009",
          "name": "Process",
          "components": [
            {
              "name": "OS API Execution",
              "detects": "Monitor for the abnormal execution of API functions associated with system logging."
            },
            {
              "name": "Process Creation",
              "detects": "Monitor newly executed processes that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms."
            },
            {
              "name": "Process Modification",
              "detects": "Using another process or third-party tools, monitor for modifications or access to system processes associated with logging."
            },
            {
              "name": "Process Termination",
              "detects": "Monitor for unexpected deletions of a running process (ex: Sysmon EID 5 or Windows EID 4689) that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms."
            }
          ]
        },
        {
          "id": "DS0012",
          "name": "Script",
          "components": [
            {
              "name": "Script Execution",
              "detects": "Monitor for any attempts to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent."
            }
          ]
        },
        {
          "id": "DS0013",
          "name": "Sensor Health",
          "components": [
            {
              "name": "Host Status",
              "detects": "Monitor logging, messaging, and other artifacts highlighting the health of host sensors (ex: metrics, errors, and/or exceptions from logging applications) that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms. Lack of log events may be suspicious."
            }
          ]
        },
        {
          "id": "DS0019",
          "name": "Service",
          "components": [
            {
              "name": "Service Metadata",
              "detects": "Monitor contextual data about a service/daemon, which may include information such as name, service executable, start type that that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms."
            }
          ]
        },
        {
          "id": "DS0002",
          "name": "User Account",
          "components": [
            {
              "name": "User Account Modification",
              "detects": "Monitor for changes to account settings associated with users/tenants that may impact defensive logging capabilities, such as the Update User and Change User License events in the Azure AD audit log."
            }
          ]
        },
        {
          "id": "DS0024",
          "name": "Windows Registry",
          "components": [
            {
              "name": "Windows Registry Key Deletion",
              "detects": "Monitor for unexpected deletion of windows registry keys that that may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms."
            },
            {
              "name": "Windows Registry Key Modification",
              "detects": "Monitor Registry edits for modifications to services and startup programs that correspond to security tools."
            }
          ]
        }
      ],
