  ],
  "mitigations": [
    {
      "id": "M1026",
      "mitigation": "Privileged Account Management",
      "description": "Restrict administrator accounts to as few individuals as possible, following least privilege principles, that may be abused to remotely boot a machine in safe mode.[4]"
    },
    {
      "id": "M1054",
      "mitigation": "Software Configuration",
      "description": "Ensure that endpoint defenses run in safe mode.[4]"
    }
  ],
  "detection": [
    {
      "id": "DS0017",
      "data-source": "Command",
      "data-component": "Command Execution",
      "detects": "Monitor executed commands and arguments associated with making configuration changes to boot settings, such as `bcdedit.exe` and `bootcfg.exe`.[3][14][2]"
    },
    {
      "id": "DS0009",
      "data-source": "Process",
      "data-component": "Process Creation",
      "detects": "Monitor newly executed processes that may abuse Windows safe mode to disable endpoint defenses."
    },
    {
      "id": "DS0024",
      "data-source": "Windows Registry",
      "data-component": "Windows Registry Key Creation",
      "detects": "Monitor Registry creation for services that may start on safe mode. For example, a program can be forced to start on safe mode boot by adding a `*` in front of the \"Startup\" value name: `HKLM\\Software\\Microsoft\\Windows\\CurrentVersion\\Run[\"*Startup\"=\"{Path}\"]` or by adding a key to `HKLM\\SYSTEM\\CurrentControlSet\\Control\\SafeBoot\\Minimal`.[6][2]"
    },
    {
      "id": "DS0024-Windows Registry Key Modification",
      "data-source": "Windows Registry",
      "data-component": "Windows Registry Key Modification",
      "detects": "Monitor modifications to Registry data associated with enabling safe mode. For example, a service can be forced to start on safe mode boot by adding a `*` in front of the \"Startup\" value name: `HKLM\\Software\\Microsoft\\Windows\\CurrentVersion\\Run[\"*Startup\"=\"{Path}\"]` or by adding a key to `HKLM\\SYSTEM\\CurrentControlSet\\Control\\SafeBoot\\Minimal`.[6][2]"
    }
  ],
