    }
  ],
  "mitigations": [
    {
      "id": "M1036",
      "name": "Account Use Policies",
      "description": "Set account lockout policies after a certain number of failed login attempts to prevent passwords from being guessed. Too strict a policy may create a denial of service condition and render environments un-usable, with all accounts used in the brute force being locked-out. Use conditional access policies to block logins from non-compliant devices or from outside defined organization IP ranges. [Source](https://learn.microsoft.com/en-us/azure/active-directory/conditional-access/concept-conditional-access-policy-common)"
    },
    {
      "id": "M1032",
      "name": "Multi-factor Authentication",
      "description": "Use multi-factor authentication. Where possible, also enable multi-factor authentication on externally facing services."
    },
    {
      "id": "M1027",
      "name": "Password Policies",
      "description": "Refer to NIST guidelines when creating password policies. [Source](https://pages.nist.gov/800-63-3/sp800-63b.html)"
    },
    {
      "id": "M1018",
      "name": "User Account Management",
      "description": "Proactively reset accounts that are known to be part of breached credentials either immediately, or after detecting bruteforce attempts."
    }
  ],
  "detection": [
    {
      "id": "DS0015",
      "data_source": "Application Log",
      "data_component": "Application Log Content",
      "detects": "Monitor authentication logs for system and application login failures of Valid Accounts. If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials."
    },
    {
      "id": "DS0017",
      "data_source": "Command",
      "data_component": "Command Execution",
      "detects": "Monitor executed commands and arguments that may use brute force techniques to gain access to accounts when passwords are unknown or when password hashes are obtained."
    },
    {
      "id": "DS0002",
      "data_source": "User Account",
      "data_component": "User Account Authentication",
      "detects": "Monitor for many failed authentication attempts across various accounts that may result from password spraying attempts. It is difficult to detect when hashes are cracked, since this is generally done outside the scope of the target network."
    }
  ],
