  "mitigations": [
    {
      "id": "M1036",
      "name": "Account Use Policies",
      "description": "Set account lockout policies after a certain number of failed login attempts to prevent passwords from being guessed. Too strict a policy may create a denial of service condition and render environments un-usable, with all accounts used in the brute force being locked-out. Use conditional access policies to block logins from non-compliant devices or from outside defined organization IP ranges. [22]",
      "url": "/mitigations/M1036"
    },
    {
      "id": "M1032",
      "name": "Multi-factor Authentication",
      "description": "Use multi-factor authentication. Where possible, also enable multi-factor authentication on externally facing services.",
      "url": "/mitigations/M1032"
    },
    {
      "id": "M1027",
      "name": "Password Policies",
      "description": "Refer to NIST guidelines when creating password policies. [23]",
      "url": "/mitigations/M1027"
    },
    {
      "id": "M1051",
      "name": "Update Software",
      "description": "Upgrade management services to the latest supported and compatible version. Specifically, any version providing increased password complexity or policy enforcement preventing default or weak passwords.",
      "url": "/mitigations/M1051"
    }
  ],
