  "mitigations": [
    {
      "ID": "M1047",
      "Mitigation": "Audit",
      "Description": "<p>Preemptively search through communication services to find shared unsecured credentials. Searching for common patterns like \"<code>password is </code>\", \"<code>password=</code>\" and take actions to reduce exposure when found.</p>"
    },
    {
      "ID": "M1017",
      "Mitigation": "User Training",
      "Description": "<p>Ensure that developers and system administrators are aware of the risk associated with sharing unsecured passwords across communication services.</p>"
    }
  ],
