  "mitigations": [
    {
      "ID": "M1038",
      "name": "Execution Prevention",
      "url": "/mitigations/M1038",
      "description": "Enable application control solutions such as AppLocker and/or Device Guard to block the loading of malicious SIP DLLs."
    },
    {
      "ID": "M1022",
      "name": "Restrict File and Directory Permissions",
      "url": "/mitigations/M1022",
      "description": "Restrict storage and execution of SIP DLLs to protected directories, such as C:\\Windows, rather than user directories."
    },
    {
      "ID": "M1024",
      "name": "Restrict Registry Permissions",
      "url": "/mitigations/M1024",
      "description": "Ensure proper permissions are set for Registry hives to prevent users from modifying keys related to SIP and trust provider components."
    }
  ],
  "detection": [
    {
      "ID": "DS0022",
      "name": "File",
      "component": "File Modification",
      "detects": "Periodically baseline registered SIPs and trust providers (Registry entries and files on disk), specifically looking for new, modified, or non-Microsoft entries."
    },
    {
      "ID": "DS0011",
      "name": "Module",
      "component": "Module Load",
      "detects": "Enable CryptoAPI v2 (CAPI) event logging to monitor and analyze error events related to failed trust validation (Event ID 81)."
    },
    {
      "ID": "DS0024",
      "name": "Windows Registry",
      "component": "Windows Registry Key Modification",
      "detects": "Enable the Registry Global Object Access Auditing setting to apply a global system access control list (SACL) and event auditing on modifications to Registry values."
    }
  ],
