  "detections": [
    {
      "id": "DS0025",
      "name": "Cloud Service",
      "components": [
        {
          "name": "Cloud Service Metadata",
          "url": "/datasources/DS0025/#Cloud%20Service%20Metadata",
          "detection": "Monitor for quota increases across all regions, especially multiple quota increases in a short period of time or quota increases in unused regions. Monitor for changes to tenant-level settings such as subscriptions and enabled regions.",
          "reference": {
            "id": 1,
            "description": "Microsoft Threat Intelligence. (2023, July 25). Cryptojacking: Understanding and defending against cloud compute resource abuse. Retrieved September 5, 2023.",
