  "mitigations": [
    {
      "id": "M1046",
      "name": "Boot Integrity",
      "description": "Some vendors of embedded network devices provide cryptographic signing to ensure the integrity of operating system images at boot time. Implement where available, following vendor guidelines.",
      "url": "/mitigations/M1046",
      "references": [
        {
          "text": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Secure Boot. Retrieved October 19, 2020.",
          "url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#35"
        }
      ]
    },
    {
      "id": "M1045",
      "name": "Code Signing",
      "description": "Many vendors provide digitally signed operating system images to validate the integrity of the software used on their platform. Make use of this feature where possible in order to prevent and/or detect attempts by adversaries to compromise the system image.",
      "url": "/mitigations/M1045",
      "references": [
        {
          "text": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Deploy Signed IOS. Retrieved October 21, 2020.",
          "url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#34"
        }
      ]
    },
    {
      "id": "M1043",
      "name": "Credential Access Protection",
      "description": "Some embedded network devices are capable of storing passwords for local accounts in either plain-text or encrypted formats. Ensure that, where available, local passwords are always encrypted, per vendor recommendations.",
      "url": "/mitigations/M1043",
      "references": [
        {
          "text": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Credentials Management. Retrieved October 19, 2020.",
          "url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#40"
        }
      ]
    },
    {
      "id": "M1032",
      "name": "Multi-factor Authentication",
      "description": "Use multi-factor authentication for user and privileged accounts. Most embedded network devices support TACACS+ and/or RADIUS. Follow vendor prescribed best practices for hardening access control.",
      "url": "/mitigations/M1032",
      "references": [
        {
          "text": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - TACACS. Retrieved October 19, 2020.",
          "url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#39"
        }
      ]
    },
    {
      "id": "M1027",
      "name": "Password Policies",
      "description": "Refer to NIST guidelines when creating password policies.",
      "url": "/mitigations/M1027",
      "references": [
        {
          "text": "Grassi, P., et al. (2017, December 1). SP 800-63-3, Digital Identity Guidelines. Retrieved January 16, 2019.",
          "url": "https://pages.nist.gov/800-63-3/sp800-63b.html"
        }
      ]
    },
    {
      "id": "M1026",
      "name": "Privileged Account Management",
      "description": "Restrict administrator accounts to as few individuals as possible, following least privilege principles. Prevent credential overlap across systems of administrator and privileged accounts, particularly between network and non-network platforms, such as servers or endpoints.",
      "url": "/mitigations/M1026"
    }
  ],
  "detection": [
    {
      "id": "DS0022",
      "data_source": "File",
      "data_component": "File Modification",
      "description": "Most embedded network devices provide a command to print the version of the currently running operating system. Use this command to query the operating system for its version number and compare it to what is expected for the device in question. Because this method may be used in conjunction with Patch System Image, it may be appropriate to also verify the integrity of the vendor provided operating system image file. Compare the checksum of the operating system file with the checksum of a known good copy from a trusted source. Some embedded network device platforms may have the capability to calculate the checksum of the file, while others may not. Even for those platforms that have the capability, it is recommended to download a copy of the file to a trusted computer to calculate the checksum with software that is not compromised. Many vendors of embedded network devices can provide advanced debugging support that will allow them to work with device owners to validate the integrity of the operating system running in memory. If a compromise of the operating system is suspected, contact the vendor technical support and seek such services for a more thorough inspection of the current running system.",
      "url": "/datasources/DS0022",
