  "mitigations": [
    {
      "id": "M1036",
      "name": "Account Use Policies",
      "description": "Enable account restrictions to prevent login attempts, and the subsequent 2FA/MFA service requests, from being initiated from suspicious locations or when the source of the login attempts do not match the location of the 2FA/MFA smart device. Use conditional access policies to block logins from non-compliant devices or from outside defined organization IP ranges.",
      "citation": {
        "author": "Microsoft",
        "date": "2022, December 14",
        "title": "Conditional Access templates",
        "retrieved": "February 21, 2023",
        "url": "https://learn.microsoft.com/en-us/azure/active-directory/conditional-access/concept-conditional-access-policy-common"
      }
    },
    {
      "id": "M1032",
      "name": "Multi-factor Authentication",
      "description": "Implement more secure 2FA/MFA mechanisms in replacement of simple push or one-click 2FA/MFA options. For example, having users enter a one-time code provided by the login screen into the 2FA/MFA application or utilizing other out-of-band 2FA/MFA mechanisms (such as rotating code-based hardware tokens providing rotating codes that need an accompanying user pin) may be more secure. Furthermore, change default configurations and implement limits upon the maximum number of 2FA/MFA request prompts that can be sent to users in period of time.",
      "citation": {
        "author": "Jessica Haworth",
        "date": "2022, February 16",
        "title": "MFA fatigue attacks: Users tricked into allowing device access due to overload of push notifications",
        "retrieved": "March 31, 2022",
        "url": "https://portswigger.net/daily-swig/mfa-fatigue-attacks-users-tricked-into-allowing-device-access-due-to-overload-of-push-notifications"
      }
    },
    {
      "id": "M1017",
      "name": "User Training",
      "description": "Train users to only accept 2FA/MFA requests from login attempts they initiated, to review source location of the login attempt prompting the 2FA/MFA requests, and to report suspicious/unsolicited prompts."
    }
  ],
  "detection": [
    {
      "id": "DS0015",
      "data_source": "Application Log",
      "data_component": "Application Log Content",
      "detects": "Monitor application logs for suspicious events including repeated MFA failures that may indicate user's primary credentials have been compromised."
    },
    {
      "id": "DS0028",
      "data_source": "Logon Session",
      "data_component": "Logon Session Creation",
      "detects": "Monitor 2FA/MFA application logs for suspicious events such as rapid login attempts with valid credentials."
    },
    {
      "id": "DS0028",
      "data_source": "Logon Session",
      "data_component": "Logon Session Metadata",
      "detects": "Monitor 2FA/MFA application logs for suspicious events such as unusual login attempt source location, mismatch in location of login attempt and smart device approving 2FA/MFA request prompts."
    },
    {
      "id": "DS0002",
      "data_source": "User Account",
      "data_component": "User Account Authentication",
      "detects": "Monitor user account logs for suspicious events: unusual login attempt source location, mismatch in location of login attempt and smart device receiving 2FA/MFA request prompts, and high volume of repeated login attempts, all of which may indicate user's primary credentials have been compromised minus 2FA/MFA mechanism."
    }
