  "mitigations": [
    {
      "id": "M1015",
      "name": "Active Directory Configuration",
      "description": "Ensure certificate authorities (CA) are properly secured, including treating CA servers (and other resources hosting CA certificates) as tier 0 assets. Harden abusable CA settings and attributes. For example, consider disabling the usage of AD CS certificate SANs within relevant authentication protocol settings to enforce strict user mappings and prevent certificates from authenticating as other identifies.[4] Also consider enforcing CA Certificate Manager approval for the templates that include SAN as an issuance requirement.",
      "url": "/mitigations/M1015"
    },
    {
      "id": "M1047",
      "name": "Audit",
      "description": "Check and remediate unneeded existing authentication certificates as well as common abusable misconfigurations of CA settings and permissions, such as AD CS certificate enrollment permissions and published overly permissive certificate templates (which define available settings for created certificates). For example, available AD CS certificate templates can be checked via the Certificate Authority MMC snap-in (certsrv.msc). certutil.exe can also be used to examine various information within an AD CS CA database.[4][11][12]",
      "url": "/mitigations/M1047"
    },
    {
      "id": "M1042",
      "name": "Disable or Remove Feature or Program",
      "description": "Consider disabling old/dangerous authentication protocols (e.g. NTLM), as well as unnecessary certificate features, such as potentially vulnerable AD CS web and other enrollment server roles.[4]",
      "url": "/mitigations/M1042"
    },
    {
      "id": "M1041",
      "name": "Encrypt Sensitive Information",
      "description": "Ensure certificates as well as associated private keys are appropriately secured. Consider utilizing additional hardware credential protections such as trusted platform modules (TPM) or hardware security modules (HSM). Enforce HTTPS and enable Extended Protection for Authentication.[4]",
      "url": "/mitigations/M1041"
    }
  ],
  "detection": [
    {
      "id": "DS0026",
      "data_source": "Active Directory",
      "data_component": "Active Directory Credential Request",
      "description": "Monitor AD CS certificate requests (ex: EID 4886) as well as issued certificates (ex: EID 4887) for abnormal activity, including unexpected certificate enrollments and signs of abuse within certificate attributes (such as abusable EKUs).[4]",
      "url": "/datasources/DS0026/#Active%20Directory%20Credential%20Request"
    },
    {
      "id": "DS0026",
      "data_source": "Active Directory",
      "data_component": "Active Directory Object Modification",
      "description": "Monitor for changes to CA attributes and settings, such as AD CS certificate template modifications (ex: EID 4899/4900 once a potentially malicious certificate is enrolled).[4]",
      "url": "/datasources/DS0026/#Active%20Directory%20Object%20Modification"
    },
    {
      "id": "DS0015",
      "data_source": "Application Log",
      "data_component": "Application Log Content",
      "description": "Ensure CA audit logs are enabled and monitor these services for signs of abuse.[4]",
      "url": "/datasources/DS0015/#Application%20Log%20Content"
    },
    {
      "id": "DS0017",
      "data_source": "Command",
      "data_component": "Command Execution",
      "description": "Monitor for the execution of commands and other utilities abused to forge and/or steal certificates and related private keys.[4]",
      "url": "/datasources/DS0017/#Command%20Execution"
    },
    {
      "id": "DS0022",
      "data_source": "File",
      "data_component": "File Access",
      "description": "Monitor for attempts to access files that store information about certificates and their associated private keys. For example, personal certificates for users may be stored on disk in folders such as %APPDATA%\\Microsoft\\SystemCertificates\\My\\Certificates\\.[4][1]",
      "url": "/datasources/DS0022/#File%20Access"
    },
    {
      "id": "DS0028",
      "data_source": "Logon Session",
      "data_component": "Logon Session Creation",
      "description": "Monitor certificate-based authentication events, such as EID 4768 when an AD CS certificate is used for Kerberos authentication (especially those that don’t correspond to legitimately issued certificates) or when Secure Channel (Schannel, associated with SSL/TLS) is highlighted as the Logon Process associated with an EID 4624 logon event.[4]",
      "url": "/datasources/DS0028/#Logon%20Session%20Creation"
    },
    {
      "id": "DS0024",
      "data_source": "Windows Registry",
      "data_component": "Windows Registry Key Access",
      "description": "Monitor for attempts to access information stored in the Registry about certificates and their associated private keys. For example, user certificates are commonly stored under HKEY_CURRENT_USER\\SOFTWARE\\Microsoft\\SystemCertificates.[4][1]",
      "url": "/datasources/DS0024/#Windows%20Registry%20Key%20Access"
    }
  ],
